    // HOVER EFFECTS
    function ThumbnailHoverEffectExtract( name, effect) {
      var easings=[ 'easeInQuad', 'easeOutQuad', 'easeInOutQuad', 'easeInCubic', 'easeOutCubic', 'easeInOutCubic', 'easeInQuart', 'easeOutQuart', 'easeInOutQuart', 'easeInQuint', 'easeOutQuint', 'easeInOutQuint', 'easeInSine', 'easeOutSine', 'easeInOutSine', 'easeInExpo', 'easeOutExpo', 'easeInOutExpo', 'easeInCirc', 'easeOutCirc', 'easeInOutCirc', 'easeOutBounce', 'easeInBack', 'easeOutBack', 'easeInOutBack', 'elastic', 'bounce'];
    
    
      var sp=name.split('_');
      if( sp.length >= 4 ) {
        // var oDef=NewTHoverEffect();
        effect.name='';
        effect.type=sp[1];
        effect.from=sp[2];
        effect.to=sp[3];
        if( sp.length >= 5 ) {
          // effect.duration=sp[4];

          for( var n=4; n<sp.length; n++ ) {
            var v=sp[n];
            
            // check if an easing name
            var foundEasing=false;
            for( var e=0; e<easings.length; e++) {
              if( v == easings[e] ) {
                foundEasing=true;
                effect.easing=v;
                break;
              }
            }
            if( foundEasing === true ) {
              continue;
            }
            
            v=v.toUpperCase();
            
            if( v == 'HOVERIN' ) {
              effect.hoverout=false;
              continue;
            }
            if( v == 'HOVEROUT' ) {
              effect.hoverin=false;
              continue;
            }
            
            if( v == 'KEYFRAME' ) {
              effect.firstKeyframe=false;
              continue;
            }
            
            var num = parseInt(v.replace(/[^0-9\.]/g, ''), 10);   // extract a number if on exists

            if( num > 0 ) {
              // the string contains a numbers > 0
              if( v.indexOf('DURATION') >= 0 ) {
                effect.duration=num;
                continue;
              }
              if( v.indexOf('DURATIONBACK') >= 0 ) {
                effect.durationBack=num;
                continue;
              }
              if( v.indexOf('DELAY') >= 0 ) {
                effect.delay=num;
                continue;
              }
              if( v.indexOf('DELAYBACK') >= 0 ) {
                effect.delayBack=num;
                continue;
              }
              
              // no parameter name found -> default is duration
              effect.duration=num;
            }
            

          }
          
          
        }        
        effect.element=ThumbnailOverEffectsGetCSSElement(sp[0], effect.type);
        
      }
      else {
        effect.name=name;
        // NanoAlert(G, 'incorrect parameter for "thumbnailHoverEffect": ' + name);
        // return null;
      }
      return effect;
    }
    
    
    function ThumbnailOverEffectsGetCSSElement( element, property ) {
        var r=element;
        
        switch ( element ) {
          case 'image':
            if( property == 'blur' || property == 'brightness' || property == 'grayscale' || property == 'sepia' || property == 'contrast' || property == 'opacity'|| property == 'saturate' ) {
              r='.nGY2GThumbnailImg';
            }
            else {
              r='.nGY2GThumbnailImage';
            }
            break;
          case 'thumbnail':
            r='.nGY2GThumbnail';
            break;
          case 'label':
            r='.nGY2GThumbnailLabel';
            break;
          case 'title':
            r='.nGY2GThumbnailTitle';
            break;
          case 'description':
            r='.nGY2GThumbnailDescription';
            break;
          case 'tools':
            r='.nGY2GThumbnailIcons';
            break;
          case 'customlayer':
            r='.nGY2GThumbnailCustomLayer';
            break;
        }
        return r;
    }
    
